#include <stdio.h>#include <windows.h>#include <cmath>double f(double x) {    return 3.0 + 2.345 * x + 0.98372 * x * x + 0.3221 * x * x * x;}int main() {	LARGE_INTEGER now;	LARGE_INTEGER then;	LARGE_INTEGER fr;	double estimat = 0.0;	double delta = 0.0;	const int fre = 50000000;	double start = 0.0;	double end = 2.0;	QueryPerformanceFrequency(&fr);	printf("CPU version begins at %d!\n", fre);	QueryPerformanceCounter(&now);	delta = (end - start) / fre;	for (int i = 0; i < fre; i++)	{		estimat = estimat + abs(delta * (f(delta * i) + f(delta * (i + 1))) / 2);	}	QueryPerformanceCounter(&then);	printf("Area is %f.\n", estimat);	printf("CPU耗时：%f毫秒\n", (double)(then.QuadPart - now.QuadPart) * 1000 / (double)(fr.QuadPart));	system("pause");    return 0;}